    /**
     * in the constructor of main class, Abstract file menu has been added to open a file and exit the program
     * Open menu , opens a file and displays the images selected to the scroll panel. 
     * There are four sort options at the top of the form to sort by names and size of the images.
     */
    
    public Sorting() {
        initComponents();
        fileMenu.add(new AbstractAction("Open") {
            @Override
            public void actionPerformed(ActionEvent e) {
                JFileChooser openFile = new JFileChooser();
                openFile.setMultiSelectionEnabled(true);
                int r = openFile.showDialog(null, JFileChooser.APPROVE_SELECTION);
                if (r == JFileChooser.APPROVE_OPTION) {
                    files = openFile.getSelectedFiles();
                    displayImages(files);                    
                }
            }
        });
        fileMenu.add(new AbstractAction("Exit") {
            @Override
            public void actionPerformed(ActionEvent e) {
                dispose();
            }
        });
    }

    private void displayImages(File[] files) {
        JPanel panel = new JPanel(new GridLayout(files.length, 1));
        for (int lop = 0; lop < files.length; lop++) {
            ImageIcon icon = new ImageIcon(files[lop].getAbsolutePath());
            JLabel label = new JLabel(icon);
            panel.add(label);
        }
        jScrollPane1.setViewportView(panel);
    }

