    private void nameAsortActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_nameAsortActionPerformed
        Arrays.sort(files, new Comparator<File>(){
            @Override
            public int compare(File o1, File o2) {
                return o1.getName().compareTo(o2.getName());
            }
        });
        displayImages(files);
        
    }//GEN-LAST:event_nameAsortActionPerformed

    private void nameDSortActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_nameDSortActionPerformed
        // TODO add your handling code here:
        Arrays.sort(files, new Comparator<File>(){
            @Override
            public int compare(File o1, File o2) {
                return o1.getName().compareTo(o2.getName())*(-1);
            }
        });
        displayImages(files);
    }//GEN-LAST:event_nameDSortActionPerformed

    private void sizeASortActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_sizeASortActionPerformed
        // TODO add your handling code here:
        Arrays.sort(files, new Comparator<File>(){
            @Override
            public int compare(File o1, File o2) {
                //throw new UnsupportedOperationException("Not supported yet."); //To change body of generated methods, choose Tools | Templates.
                return (int)(o1.length()- o2.length());
            }
        });
        displayImages(files); 
    }//GEN-LAST:event_sizeASortActionPerformed

    private void sizeDSortActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_sizeDSortActionPerformed
        // TODO add your handling code here:
        Arrays.sort(files, new Comparator<File>(){
            @Override
            public int compare(File o1, File o2) {
                //throw new UnsupportedOperationException("Not supported yet."); //To change body of generated methods, choose Tools | Templates.
                return (int)((o1.length()- o2.length())*(-1));
            }
        });
        displayImages(files);
    }//GEN-LAST:event_sizeDSortActionPerformed

